mixins.code:
@mixin react-md-accessibility {
  .md-fake-btn {
    background: transparent;
    position: relative;

    &--no-outline {
      outline-style: none;
    }
  }

  // Need to add one additional level of precedence for some dialogs,
  // but don't want to use !important
  .md-no-scroll.md-no-scroll {
    overflow: hidden;
    position: fixed;
  }

  .md-pointer--hover:hover {
    cursor: pointer;
  }

  .md-pointer--none {
    pointer-events: none;
  }

  .md-content-jump {
    left: $md-content-jump-left-inactive;
    position: absolute;
    top: $md-content-jump-top-inactive;

    &:active,
    &:focus {
      left: $md-content-jump-left-active;
      top: $md-content-jump-top-active;
    }
  }
}

@mixin md-ios-scroll-momentum($enable-momentum: $md-enable-ios-scroll-momentum) {
  @if $enable-momentum {
    -webkit-overflow-scrolling: touch;
  }
}

@mixin react-md-autocompletes($light-theme: $md-light-theme, $include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    .md-autocomplete-container {
      position: relative;
    }

    .md-autocomplete-suggestion {
      color: get-color('secondary', $light-theme);
      line-height: $md-text-field-line-height;
      overflow: hidden;
      position: absolute;
      top: $md-autocomplete-suggestion-top;
      white-space: nowrap;
    }

    @if $include-media {
      @include react-md-autocompletes-media;
    }
  }
}

@mixin react-md-autocompletes-mobile($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    .md-autocomplete-suggestion {
      font-size: $md-text-field-mobile-font-size;

      &--floating {
        top: $md-autocomplete-suggestion-mobile-floating-top;
      }

      &--block {
        top: $md-autocomplete-suggestion-mobile-block-top;
      }
    }
  }
}

@mixin react-md-autocompletes-desktop($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    .md-autocomplete-suggestion {
      font-size: $md-text-field-desktop-font-size;

      &--floating {
        top: $md-autocomplete-suggestion-desktop-floating-top;
      }

      &--block {
        top: $md-autocomplete-suggestion-desktop-block-top;
      }
    }
  }
}

@mixin react-md-autocompletes-media($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    @media #{$md-mobile-media} {
      @include react-md-autocompletes-mobile($include-inline);
    }

    @media #{$md-desktop-media} {
      @include react-md-autocompletes-desktop($include-inline);
    }
  }
}

@mixin react-md-theme-autocompletes($light-theme: $md-light-theme, $include-inline: $md-inline-autocomplete) {
  @if $light-theme != $md-light-theme and $include-inline {
    .md-autocomplete-suggestion {
      color: get-color('secondary', $light-theme);
    }
  }
}

@mixin react-md-autocompletes-mobile($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    .md-autocomplete-suggestion {
      font-size: $md-text-field-mobile-font-size;

      &--floating {
        top: $md-autocomplete-suggestion-mobile-floating-top;
      }

      &--block {
        top: $md-autocomplete-suggestion-mobile-block-top;
      }
    }
  }
}

@mixin react-md-autocompletes-desktop($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    .md-autocomplete-suggestion {
      font-size: $md-text-field-desktop-font-size;

      &--floating {
        top: $md-autocomplete-suggestion-desktop-floating-top;
      }

      &--block {
        top: $md-autocomplete-suggestion-desktop-block-top;
      }
    }
  }
}

@mixin react-md-autocompletes-media($include-inline: $md-inline-autocomplete) {
  @if $include-inline {
    @media #{$md-mobile-media} {
      @include react-md-autocompletes-mobile($include-inline);
    }

    @media #{$md-desktop-media} {
      @include react-md-autocompletes-desktop($include-inline);
    }
  }
}

@mixin react-md-avatar-media($include-icon-sized: $md-avatar-include-icon-sized, $include-dense-icons: $md-font-icon-include-dense) {
  @if $include-icon-sized and $include-dense-icons and $md-font-icon-size != $md-font-icon-dense-size {
    @media #{$md-desktop-media} {
      .md-avatar-content {
        font-size: $md-font-icon-dense-size;
      }

      .md-avatar--icon-sized {
        height: $md-font-icon-dense-size;
        width: $md-font-icon-dense-size;
      }
    }
  }
}

@mixin react-md-buttons-mobile($include-flat: $md-btn-include-flat, $include-raised: $md-btn-include-raised, $include-floating: $md-btn-include-floating) {
  @if $include-flat or $include-raised {
    @if $md-btn-text-height == null {
      $accessible-padding: ($md-btn-accessible-height - $md-btn-mobile-height) / 2;

      .md-btn--text {
        height: $md-btn-mobile-height;
        margin-bottom: $accessible-padding;
        margin-top: $accessible-padding;

        @if $md-btn-text-height == null {
          font-size: $md-btn-mobile-font-size;
        }

        // The accessible height is supposed to be 48px while the button height is supposed
        // to be 36px.. So hack for touch by adding some margin for the difference and adding
        // ::before/::after absolutely positioned so they can be touched as well? Iunno
        &::before,
        &::after {
          content: '';
          height: $accessible-padding;
          left: 0;
          position: absolute;
          right: 0;
        }

        &::before {
          top: -$accessible-padding;
        }

        &::after {
          bottom: -$accessible-padding;
        }
      }
    } @else if $md-btn-text-font-size == null {
      .md-btn--text {
        font-size: $md-btn-mobile-font-size;
      }
    }
  }

  @if $include-floating and $md-btn-floating-margin == null {
    @include react-md-button-fixed-positions($md-btn-mobile-floating-margin);
  }
}

@mixin react-md-buttons-desktop($include-flat: $md-btn-include-flat, $include-raised: $md-btn-include-raised, $include-icon: $md-btn-include-icon, $include-floating: $md-btn-include-floating, $include-dense-icons: $md-font-icon-include-dense) {
  @if $include-flat or $include-raised {
    @if $md-btn-text-height == null {
      .md-btn--text {
        height: $md-btn-desktop-height;
        margin-bottom: 0;
        margin-top: 0;

        @if $md-btn-text-height == null {
          font-size: $md-btn-desktop-font-size;
        }

        &::before,
        &::after {
          display: none;
          visibility: hidden;
        }

        &::before {
          top: 0;
        }

        &::after {
          bottom: 0;
        }
      }
    } @else if $md-btn-text-font-size == null {
      .md-btn--text {
        font-size: $md-btn-desktop-font-size;
      }
    }
  }

  @if $include-floating and $md-btn-floating-margin == null {
    @include react-md-button-fixed-positions($md-btn-desktop-floating-margin);
  }

  @if ($include-icon or $include-floating) and $include-dense-icons {
    $icon-size-diff: $md-btn-icon-size != $md-btn-icon-dense-size;
    $icon-pad-diff: $md-btn-icon-padding != $md-btn-icon-dense-padding;
    @if ($icon-size-diff or $icon-pad-diff) {
      .md-btn--icon {
        @if $icon-size-diff {
          height: $md-btn-icon-dense-size;
          width: $md-btn-icon-dense-size;
        }

        @if $icon-pad-diff {
          padding: $md-btn-icon-dense-padding;
        }
      }
    }

    @if $md-btn-floating-size != $md-btn-floating-dense-size {
      .md-btn--floating {
        height: $md-btn-floating-dense-size;
        padding: ($md-btn-floating-dense-size - $md-font-icon-dense-size) / 2;
        width: $md-btn-floating-dense-size;

        &-mini {
          height: $md-btn-floating-mini-size;
          padding: ($md-btn-floating-mini-size - $md-font-icon-dense-size) / 2;
          width: $md-btn-floating-mini-size;
        }
      }
    }
  }
}

@mixin react-md-buttons-media($include-flat: $md-btn-include-flat, $include-raised: $md-btn-include-raised, $include-icon: $md-btn-include-icon, $include-floating: $md-btn-include-floating, $include-dense-icons: $md-font-icon-include-dense) {
  @media #{$md-mobile-media} {
    @include react-md-buttons-mobile($include-flat, $include-raised, $include-floating);
  }

  @media #{$md-desktop-media} {
    @include react-md-buttons-desktop($include-flat, $include-raised, $include-icon, $include-floating, $include-dense-icons);
  }
}

@mixin react-md-drawers-mobile {
  .md-drawer {

    &--left {
      max-width: $md-drawer-mobile-max-width;
      transform: translate3d(-100%, 0, 0);
      width: calc(100vw - #{$md-drawer-mobile-margin});
    }

    &--right {
      left: 0;
      transform: translate3d(100%, 0, 0);
    }

    &--mini.md-drawer--mini {
      width: $md-drawer-mobile-mini-width;
    }
  }

  .md-list-tile--mini.md-list-tile--mini {
    padding-left: $md-drawer-mobile-mini-item-padding;
    padding-right: $md-drawer-mobile-mini-item-padding;
  }

  .md-drawer-relative--mini.md-drawer-relative--mini {
    margin-left: $md-drawer-mobile-mini-width;
  }

  .md-toolbar ~ .md-list--drawer {
    height: calc(100% - #{$md-toolbar-mobile-portrait-height});
  }
}

@mixin react-md-drawers-mobile-landscape {
  .md-toolbar ~ .md-list--drawer {
    height: calc(100% - #{$md-toolbar-mobile-landscape-height});
  }
}

@mixin react-md-drawers-tablet {
  .md-drawer {

    &--left {
      max-width: $md-drawer-desktop-left-max-width;
      transform: translate3d(-$md-drawer-desktop-width, 0, 0);
      width: $md-drawer-tablet-width;
    }

    &--right {
      transform: translate3d(100%, 0, 0);
    }

    &--mini.md-drawer--mini {
      width: $md-drawer-desktop-mini-width;
    }
  }

  .md-list-tile--mini.md-list-tile--mini {
    padding-left: $md-drawer-desktop-mini-item-padding;
    padding-right: $md-drawer-desktop-mini-item-padding;
  }

  .md-drawer-relative {
    margin-left: $md-drawer-tablet-width;
  }

  .md-drawer-relative--mini.md-drawer-relative--mini {
    margin-left: $md-drawer-desktop-mini-width;
  }

  .md-toolbar ~ .md-list--drawer {
    height: calc(100% - #{$md-toolbar-tablet-height});
  }
}

@mixin react-md-drawers-desktop {
  $diff-width: $md-drawer-tablet-width != $md-drawer-desktop-width;

  .md-drawer {
    @if $diff-width {
      width: $md-drawer-desktop-width;
    }
  }

  @if $diff-width {
    .md-drawer-relative {
      margin-left: $md-drawer-desktop-width;
    }
  }
}

@mixin react-md-drawers-media {
  @media #{$md-mobile-media} and (max-width: #{$md-desktop-min-width - 1}) {
    @include react-md-drawers-mobile;
  }

  @media #{$md-mobile-landscape-media} {
    @include react-md-drawers-mobile-landscape;
  }

  @media #{$md-tablet-media} {
    @include react-md-drawers-tablet;
  }

  @media #{$md-desktop-media} {
    @include react-md-drawers-desktop;
  }
}

@mixin react-md-grid-mobile($columns: $md-grid-phone-columns, $margin: $md-grid-phone-margin, $gutter: $md-grid-phone-gutter) {
  @include react-md-make-grid('phone', $columns, $margin, $gutter);
}

@mixin react-md-grid-tablet($columns: $md-grid-tablet-columns, $margin: $md-grid-tablet-margin, $gutter: $md-grid-tablet-gutter) {
  @include react-md-make-grid('tablet', $columns, $margin, $gutter);
}

@mixin react-md-grid-desktop($columns: $md-grid-desktop-columns, $margin: $md-grid-desktop-margin, $gutter: $md-grid-desktop-gutter) {
  @include react-md-make-grid('desktop', $columns, $margin, $gutter);
}

@mixin react-md-grid-media {
  @media (max-width: $md-grid-tablet-breakpoint - 1) {
    @include react-md-grid-mobile;
  }

  @media (min-width: $md-grid-tablet-breakpoint) and (max-width: $md-grid-desktop-breakpoint - 1) {
    @include react-md-grid-tablet;
  }

  @media (min-width: $md-grid-desktop-breakpoint) {
    @include react-md-grid-desktop;
  }
}

@mixin react-md-lists-mobile($include-icon: $md-list-include-icon, $include-avatar: $md-list-include-avatar, $include-two-lines: $md-list-include-two-lines, $include-three-lines: $md-list-include-three-lines) {
  @if $md-list-padding == null {
    .md-list {
      padding-bottom: $md-list-mobile-padding;
      padding-top: $md-list-mobile-padding;
    }
  }

  @if $md-list-height == null {
    .md-list-tile {
      height: $md-list-mobile-height;
    }
  }

  @if $md-list-primary-font-size == null {
    .md-tile-text--primary {
      font-size: $md-list-mobile-primary-font-size;
    }
  }

  @if $include-two-lines or $include-three-lines and $md-list-secondary-font-size == null {
    .md-tile-text--secondary {
      font-size: $md-list-mobile-secondary-font-size;
    }
  }

  @if $include-avatar and $md-list-avatar-height == null {
    .md-list-tile--avatar {
      height: $md-list-mobile-avatar-height;
    }
  }

  @if $include-two-lines and $md-list-two-lines-height == null {
    .md-list-tile--two-lines {
      height: $md-list-mobile-two-lines-height;
    }
  }

  @if $include-three-lines and $md-list-three-lines-height == null {
    .md-list-tile--three-lines {
      height: $md-list-mobile-three-lines-height;

      .md-tile-text--secondary {
        height: $md-line-height * $md-list-mobile-secondary-font-size * 2;
      }

      @if $include-icon or $include-avatar {
        .md-tile-addon {
          margin-top: $md-list-mobile-three-lines-addon-margin-top;
        }
      }
    }
  }
}

@mixin react-md-lists-desktop($include-icon: $md-list-include-icon, $include-avatar: $md-list-include-avatar, $include-two-lines: $md-list-include-two-lines, $include-three-lines: $md-list-include-three-lines, $include-dense-icons: $md-font-icon-include-dense) {
  @if $md-list-padding == null {
    .md-list {
      padding-bottom: $md-list-desktop-padding;
      padding-top: $md-list-desktop-padding;
    }
  }

  @if $md-list-height == null {
    .md-list-tile {
      height: $md-list-desktop-height;
    }
  }

  %text-size {
    font-size: $md-list-desktop-font-size;
  }

  @if $md-list-primary-font-size == null {
    .md-tile-text--primary {
      @extend %text-size;
    }
  }

  @if $include-two-lines or $include-three-lines and $md-list-secondary-font-size == null {
    .md-tile-text--secondary {
      @extend %text-size;
    }
  }

  @if $include-avatar and $md-list-avatar-height == null {
    .md-list-tile--avatar {
      height: $md-list-desktop-avatar-height;
    }
  }

  @if $include-two-lines and $md-list-two-lines-height == null {
    .md-list-tile--two-lines {
      height: $md-list-desktop-two-lines-height;
    }
  }

  @if $include-three-lines and $md-list-three-lines-height == null {
    .md-list-tile--three-lines {
      height: $md-list-desktop-three-lines-height;

      .md-tile-text--secondary {
        height: $md-line-height * $md-list-desktop-font-size * 2;
      }

      @if $include-icon or $include-avatar {
        .md-tile-addon {
          margin-top: $md-list-desktop-three-lines-addon-margin-top;
        }
      }
    }
  }

  @if $include-dense-icons and $include-icon {
    .md-tile-addon--icon {
      height: $md-font-icon-dense-size;
    }

    .md-tile-content--left-icon {
      padding-left: $md-list-left-padding - $md-font-icon-dense-size - $md-default-padding;
    }
  }
}

@mixin react-md-lists-media($include-icon: $md-list-include-icon, $include-avatar: $md-list-include-avatar, $include-two-lines: $md-list-include-two-lines, $include-three-lines: $md-list-include-three-lines, $include-dense-icons: $md-font-icon-include-dense) {
  $media-diff: $md-list-padding == null or $md-list-primary-font-size == null
    or $md-list-secondary-font-size == null or $md-list-height == null
    or $md-list-avatar-height == null or $md-list-two-lines-height == null
    or $md-list-three-lines-height == null;

  @if $media-diff {
    @media #{$md-mobile-media} {
      @include react-md-lists-mobile($include-icon, $include-avatar, $include-two-lines, $include-three-lines);
    }
  }

  @if $media-diff or ($include-icon and $md-font-icon-size != $md-font-icon-dense-size) {
    @media #{$md-desktop-media} {
      @include react-md-lists-desktop($include-icon, $include-avatar, $include-two-lines, $include-three-lines, $include-dense-icons);
    }
  }
}

@mixin react-md-icons-media($include-dense: $md-font-icon-include-dense, $include-dense-material-icons: $md-font-icon-include-dense-material-icons) {
  @if $include-dense {
    @media #{$md-desktop-media} {
      @include react-md-icons-dense($include-dense-material-icons);
    }
  }
}

@mixin react-md-menus-mobile {
  @if $md-menu-max-width == null or $md-menu-min-width == null {
    .md-list--menu {
      max-width: if($md-menu-max-width == null, $md-menu-mobile-max-width, null);
      min-width: if($md-menu-min-width == null, $md-menu-mobile-min-width, null);
    }
  }

  @if $md-menu-max-height == null {
    .md-list--menu-restricted {
      max-height: $md-menu-mobile-max-height;
    }
  }
}

@mixin react-md-menus-desktop {
  @if $md-menu-max-width == null or $md-menu-min-width == null {
    .md-list--menu {
      max-width: if($md-menu-max-width == null, $md-menu-desktop-max-width, null);
      min-width: if($md-menu-min-width == null, $md-menu-desktop-min-width, null);
    }
  }

  @if $md-menu-max-height == null {
    .md-list--menu-restricted {
      max-height: $md-menu-desktop-max-height;
    }
  }
}

@mixin react-md-menus-media {
  @if $md-menu-max-height == null or $md-menu-max-width == null or $md-menu-min-width == null {
    @media #{$md-mobile-media} {
      @include react-md-menus-mobile;
    }

    @media #{$md-desktop-media} {
      @include react-md-menus-desktop;
    }
  }
}

@mixin react-md-navigation-drawers-mobile($enforce-height: $md-navigation-drawer-enforce-height) {
  .md-navigation-drawer-content {
    @include react-md-navigation-drawer-height($md-toolbar-mobile-portrait-height, $enforce-height);
  }
}

@mixin react-md-navigation-drawers-mobile-landscape($enforce-height: $md-navigation-drawer-enforce-height) {
  @if $md-toolbar-height == null {
    .md-navigation-drawer-content {
      @include react-md-navigation-drawer-height($md-toolbar-mobile-landscape-height, $enforce-height);
    }
  }

  @if $md-toolbar-prominent-height == null and $md-toolbar-mobile-prominent-height != null {
    .md-navigation-drawer-content--prominent-offset {
      @include react-md-navigation-drawer-height($md-toolbar-mobile-prominent-height, $enforce-height);
    }
  }
}

@mixin react-md-navigation-drawers-tablet($enforce-height: $md-navigation-drawer-enforce-height) {
  @if $md-toolbar-height == null {
    .md-navigation-drawer-content {
      @include react-md-navigation-drawer-height($md-toolbar-tablet-height, $enforce-height);
    }
  }

  @if $md-toolbar-prominent-height == null and $md-toolbar-tablet-prominent-height != null {
    .md-navigation-drawer-content {
      @include react-md-navigation-drawer-height($md-toolbar-tablet-prominent-height, $enforce-height);
    }
  }

  .md-title.md-title--persistent-offset {
    margin-left: $md-drawer-tablet-width - $md-btn-icon-size - $md-btn-icon-padding + $md-navigation-drawer-title-offset;
  }
}

@mixin react-md-navigation-drawers-desktop($enforce-height: $md-navigation-drawer-enforce-height, $include-dense-icons: $md-font-icon-include-dense) {
  @if $md-toolbar-height == null and $md-toolbar-desktop-height != null {
    .md-navigation-drawer-content {
      @include react-md-navigation-drawer-height($md-toolbar-desktop-height, $enforce-height);
    }
  }

  @if $md-toolbar-prominent-height == null and $md-toolbar-desktop-prominent-height != null {
    .md-navigation-drawer-content {
      @include react-md-navigation-drawer-height($md-toolbar-desktop-prominent-height, $enforce-height);
    }
  }

  @if $include-dense-icons or $md-drawer-tablet-width != $md-drawer-desktop-width {
    .md-title.md-title--persistent-offset {
      margin-left: $md-drawer-desktop-width - if($include-dense-icons, $md-btn-icon-dense-size, $md-btn-icon-size) - if($include-dense-icons, $md-btn-icon-dense-padding, $md-btn-icon-padding) + $md-navigation-drawer-title-offset;
    }
  }
}

@mixin react-md-navigation-drawers-media($enforce-height: $md-navigation-drawer-enforce-height, $include-dense-icons: $md-font-icon-include-dense) {
  $defaults-not-set: $md-toolbar-height == null or $md-toolbar-prominent-height == null;
  @if $defaults-not-set {
    @media #{$md-mobile-media} {
      @include react-md-navigation-drawers-mobile($enforce-height);
    }

    @media #{$md-mobile-landscape-media} {
      @include react-md-navigation-drawers-mobile-landscape($enforce-height);
    }

    @media #{$md-tablet-media} {
      @include react-md-navigation-drawers-tablet($enforce-height);
    }
  }

  @if $defaults-not-set or $include-dense-icons or $md-drawer-tablet-width != $md-drawer-desktop-width {
    @media #{$md-desktop-media} {
      @include react-md-navigation-drawers-desktop($enforce-height, $include-dense-icons);
    }
  }
}

@mixin react-md-pickers-media {
  @if $include-date or $include-time {
    @media (orientation: portrait) {
      .md-picker {
        @include react-md-pickers-make-portrait($include-date, $include-time);
