      margin-left: $md-dialog-btn-padding;
    }
  }

  .md-card-title--contextual {
    background: get-swatch($secondary-color, 50, false, $fallback-color, md-data-table-contextual-fallback-color);
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%;
    z-index: $md-data-table-card-header-z-index;
  }

  .md-card-title--title-contextual {
    color: $secondary-color;
    font-size: $md-data-table-card-header-font-size;
    font-weight: $md-font-medium;
    line-height: $md-data-table-card-header-height;
  }

  .md-drop-down-enter {
    transform: translate3d(0, -100%, 0);

    &.md-drop-down-enter-active {
      @extend %md-transition--deceleration;

      transform: translate3d(0, 0, 0);
      transition-duration: $md-transition-time;
      transition-property: transform;
    }
  }

  .md-drop-down-leave {
    transform: translate3d(0, 0, 0);

    &.md-drop-down-leave-active {
      @extend %md-transition--acceleration;

      transform: translate3d(0, -100%, 0);
      transition-duration: $md-transition-time;
      transition-property: transform;
    }
  }
}

@mixin react-md-data-table-paginations-shrink($shrink: $md-data-table-pagination-mobile-shrinking, $hide-label: $md-data-table-pagination-mobile-label-hidden) {
  @if $shrink {
    .md-table-pagination .md-text-field {
      font-size: 13px;
    }

    .md-table-pagination .md-icon-text:first-child {
      padding-right: $md-default-padding / 4;
    }
  }

  @if $hide-label {
    .md-table-pagination__label {
      display: none;
    }
  }
}

@mixin react-md-theme-data-tables($secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $include-table-card-headers: $md-data-table-include-table-card-headers, $contextual-fallback-color: $md-data-table-contextual-fallback-color, $selected-over-hover: $md-data-table-selected-over-hover) {
  @if $light-theme != $md-light-theme {
    thead .md-table-row,
    tbody .md-table-row:not(:last-child) {
      border-bottom-color: get-color('divider', $light-theme);
    }

    .md-table-row {
      $active-color: if($light-theme, $md-data-table-selected-color, $md-data-table-dark-theme-selected-color);
      $hover-color: if($light-theme, $md-data-table-hover-color, $md-data-table-dark-theme-hover-color);

      @if not $selected-over-hover {
        &--active {
          background: $active-color;
        }
      }

      @media (min-device-width: #{$md-desktop-min-width}) {
        &--hover {
          background: $hover-color;
        }
      }

      @if $selected-over-hover {
        &--active {
          background: $active-color;
        }
      }
    }
  }

  @if $include-table-card-headers {
    $bg-color: get-swatch($secondary-color, 50, false, $contextual-fallback-color, md-data-table-contextual-fallback-color);
    @if get-swatch($md-secondary-color, 50, false, $contextual-fallback-color, md-data-table-contextual-fallback-color) != $bg-color {
      .md-card-title--contextual {
        background: $bg-color;
      }
    }

    @if $secondary-color != $md-secondary-color {
      .md-card-title--title-contextual {
        color: $secondary-color;
      }
    }
  }
}

@mixin react-md-chips($light-theme: $md-light-theme, $include-media: $md-media-included, $include-remove: $md-chip-include-remove, $include-avatar: $md-chip-include-avatar, $include-contact: $md-chip-include-contact, $include-dense-icons: $md-font-icon-include-dense) {
  .md-chip {
    align-items: center;
    background: if($light-theme, $md-chip-light-theme-bg-color, $md-chip-dark-theme-bg-color);
    border: 0;
    border-radius: $md-chip-height / 2;
    display: inline-flex;
    height: $md-chip-height;
    padding-left: $md-chip-default-padding;
    padding-right: $md-chip-default-padding;
    position: relative;
    transition-duration: $md-transition-time;
    transition-property: box-shadow, background;
    vertical-align: top;
    white-space: nowrap;

    &:focus {
      @include md-box-shadow(1);
      outline-style: none;
    }
  }

  .md-chip--hover.md-chip--hover {
    background: $md-grey-700;
  }

  .md-chip-text {
    color: if($light-theme, $md-chip-light-theme-color, $md-chip-dark-theme-color);
    font-size: $md-chip-font-size;
    transition-duration: $md-transition-time;
    transition-property: color;
  }

  @if $include-avatar or $include-contact {
    @include react-md-chips-avatar;
  }

  @if $include-remove {
    @include react-md-chips-remove;
  }

  @if $include-contact {
    @include react-md-chips-contact;
  }

  // has to go last so it has higher precedence.
  .md-chip-text--hover {
    color: $md-white-base;
  }

  @if $include-media and $include-remove and $include-dense-icons {
    @media #{$md-desktop-media} {
      .md-chip-icon {
        margin-left: $md-chip-icon-dense-padding;
        margin-right: $md-chip-icon-dense-padding;
        top: ($md-chip-height - $md-font-icon-dense-size) / 2;
      }
    }
  }
}

@mixin react-md-chips-avatar {
  .md-chip .md-avatar {
    border: 0;
    height: $md-chip-height;
    left: 0;
    position: absolute;
    top: 0;
    width: $md-chip-height;
  }

  .md-chip--avatar {
    padding-left: $md-chip-height + $md-chip-avatar-left-padding;
  }
}

@mixin react-md-chips-remove {
  .md-chip-icon {
    color: $md-chip-icon-color;
    margin-left: $md-chip-icon-padding;
    margin-right: $md-chip-icon-padding;
    position: absolute;
    right: 0;
    top: ($md-chip-height - $md-font-icon-size) / 2;
    z-index: 1;
  }

  .md-chip-icon--rotate {
    transform: rotate3d(0, 0, 1, 45deg);
  }

  .md-chip--remove {
    padding-right: $md-font-icon-size + ($md-chip-icon-padding * 2);
  }
}

@mixin react-md-chips-contact {
  .md-chip-text--contact {
    font-size: $md-chip-contact-font-size;
  }
}

@mixin react-md-theme-chips($light-theme: $md-light-theme) {
  @if $light-theme != $md-light-theme {
    .md-chip {
      background: if($light-theme, $md-chip-light-theme-bg-color, $md-chip-dark-theme-bg-color);
    }

    .md-chip-text {
      color: if($light-theme, $md-chip-light-theme-color, $md-chip-dark-theme-color);
    }
  }
}

@mixin react-md-collapsers {
  .md-collapser {

    &.md-collapser {
      transition-duration: $md-transition-time;
      transition-property: transform;
    }

    .md-tooltip-container {
      transform: rotate3d(0, 0, 1, 0deg);
      transition-duration: $md-transition-time;
      transition-property: transform;
    }

    &--flipped {
      transform: rotate3d(0, 0, 1, 180deg);

      .md-tooltip-container {
        transform: rotate3d(0, 0, 1, -180deg);
      }
    }
  }
}

