@function get-color($name, $light-theme) {
  $map: if($light-theme, $md-light-theme-colors, $md-dark-theme-colors);
  @if map-has-key($map, $name) == false {
    @warn "The given color: #{$name} is not a valid color in the #{if($light-theme, 'light', 'dark')} theme.";
    @return null;
  }

  @return map-get($map, $name);
}

@function get-swatch($color, $swatch, $accent: false, $fallback-color: null, $fallback-name: null) {
  $index: index(map-values($md-color-map), $color);
  @if $index == null or $index < 1 {
    @if $fallback-color == null and $md-colors-warn-md {
      $msg: 'The given color "#{$color}" is not a valid material design color. Please set';
      @if $fallback-name {
        $msg: '#{$msg} the "$#{$fallback-name}"';
      } @else {
        $msg: '#{$msg} a fallback-color';
      }

      $msg: '#{$msg} or disable the "$md-colors-warn-md" variable to ignore this message (not recommended because of losing styling).';
      @warn "#{$msg} See https://react-md.dev/v1/customization/colors?tab=1#variable-md-colors-warn-md for more information.";
      @return $color;
    } @elseif $fallback-color == null {
      @return $color;
    }

    @return $fallback-color;
  }

  $current-color-name: nth(map-keys($md-color-map), $index);
  $colors: (
    'red'
    'pink'
    'purple'
    'deep-purple'
    'indigo'
    'blue'
    'light-blue'
    'cyan'
    'teal'
    'green'
    'light-green'
    'lime'
    'yellow'
    'amber'
    'orange'
    'deep-orange'
    'brown'
    'grey'
    'blue-grey'
  );
  $color-name: null;
  $index: 1;
  @while $color-name == null and $index < length($colors) {
    $md-color: nth($colors, $index);
    @if str-index($current-color-name, $md-color) != null {
      $color-name: $md-color;
    }

    $index: $index + 1;
  }

  @return map-get($md-color-map, "md-#{$color-name}-#{if($accent, 'a-', '')}#{$swatch}");
}

mixins.code:
@mixin react-md-colors-hover($primary-color: $md-primary-color, $primary-opacity: $md-primary-hover-opacity, $secondary-color: $md-secondary-color, $secondary-opacity: $md-secondary-hover-opacity) {
  @media #{$md-desktop-media} {
    .md-background--primary-hover:hover {
      background: rgba($primary-color, $primary-opacity);
    }

    .md-background--secondary-hover:hover {
      background: rgba($secondary-color, $secondary-opacity);
    }
  }
}

@mixin react-md-color-class-names($include-text: true, $include-background: true) {
  @each $name, $color in $md-color-map {
    @include react-md-color-class-name($name, $color, $include-text, $include-background);
  }
}

@mixin react-md-color-class-name($name, $color, $include-text: true, $include-background: true) {
  @if $include-text {
    .#{$name}--text {
      color: $color;
    }
  }

  @if $include-background {
    .#{$name}--background {
      background: $color;
    }
  }
}

@mixin react-md-colors($primary-color: $md-primary-color, $secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $include-media: $md-media-included, $include-color-class-names: $md-colors-include-class-names, $primary-opacity: $md-primary-hover-opacity, $primary-background-text-color: $md-primary-background-text-color, $secondary-opacity: $md-secondary-hover-opacity, $secondary-background-text-color: $md-secondary-background-text-color, $error-color: $md-error-color) {
  .md-text {
    color: get-color('text', $light-theme);

    &--secondary {
      color: get-color('secondary', $light-theme);
    }

    &--disabled {
      color: get-color('disabled', $light-theme);
    }

    &--theme-primary {
      color: $primary-color;
    }

    &--theme-secondary {
      color: $secondary-color;
    }

    &--error {
      color: $error-color;
    }

    &--inherit.md-text--inherit {
      color: inherit;
    }
  }

  .md-ink--primary .md-ink {
    background: rgba($primary-color, .26);
  }

  .md-ink--secondary .md-ink {
    background: rgba($secondary-color, .26);
  }

  .md-background {
    background: get-color('background', $light-theme);

    &--card {
      background: get-color('card', $light-theme);
    }

    &--primary {
      background: $primary-color;
      color: $primary-background-text-color;
    }

    &--secondary {
      background: $secondary-color;
      color: $secondary-background-text-color;
    }
  }

  @if $include-media {
    @include react-md-colors-hover($primary-color, $primary-opacity, $secondary-color, $secondary-opacity);
  }

  @if $include-color-class-names {
    @include react-md-color-class-names;
  }
}

@mixin react-md-theme-colors($primary-color: $md-primary-color, $secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $include-media: $md-media-included, $primary-opacity: $md-primary-hover-opacity, $primary-background-text-color: $md-primary-background-text-color, $secondary-opacity: $md-secondary-hover-opacity, $secondary-background-text-color: $md-secondary-background-text-color, $error-color: $md-error-color) {
  @if $primary-color != $md-primary-color {
    .md-text--theme-primary {
      color: $primary-color;
    }

    .md-ink--primary .md-ink {
      background: rgba($primary-color, .26);
    }

    .md-background--primary {
      background: $primary-color;

      @if $primary-background-text-color != $md-primary-background-text-color {
        color: $primary-background-text-color;
      }
    }
  } @else if $primary-background-text-color != $md-primary-background-text-color {
    .md-background--primary {
      color: $primary-background-text-color;
    }
  }

  @if $secondary-color != $md-secondary-color {
    .md-text--theme-secondary {
      color: $secondary-color;
    }

    .md-ink--secondary .md-ink {
      background: rgba($secondary-color, .26);
    }

    .md-background--secondary {
      background: $secondary-color;

      @if $secondary-background-text-color != $md-secondary-background-text-color {
        color: $secondary-background-text-color;
      }
    }
  } @else if $secondary-background-text-color != $md-secondary-background-text-color {
    .md-background--secondary {
      color: $secondary-background-text-color;
    }
  }

  @if $error-color != $md-error-color {
    .md-text--error {
      color: $error-color;
    }
  }

  @if $light-theme != $md-light-theme {
    .md-text {
      color: get-color('text', $light-theme);

      &--secondary {
        color: get-color('secondary', $light-theme);
      }

      &--disabled {
        color: get-color('disabled', $light-theme);
      }
    }

    .md-background {
      background: get-color('background', $light-theme);

      &--card {
        background: get-color('card', $light-theme);
      }
    }
  }

  @if $include-media and ($primary-color != $md-primary-color or $secondary-color != $md-secondary-color) {
    @include react-md-colors-hover($primary-color, $primary-opacity, $secondary-color, $secondary-opacity);
  }
}

@mixin react-md-dialogs {
  .md-dialog-container.md-overlay {
    transition-duration: $md-dialog-transition-time;
    z-index: $md-dialog-overlay-z-index;

    @if $md-dialog-use-flex-positioning {
      align-items: center;
      display: flex;
      justify-content: center;
    }
  }

  .md-dialog {
    @include react-md-dialog-width;

    cursor: auto;
    position: fixed;
  }

  .md-dialog--centered {
    @if not $md-dialog-use-flex-positioning {
      left: 50%;
      top: 50%;
      transform: translate3d(-50%, -50%, 0);
    }

    max-height: calc(100% - #{$md-dialog-vertical-keyline * 2});
    max-width: calc(100% - #{$md-dialog-horizontal-keyline * 2});

    .md-list {
      padding-bottom: $md-dialog-padding - $md-default-padding;
      padding-top: 0;
    }

    .md-list-tile {
      height: auto;
      padding: $md-default-padding $md-dialog-padding;
    }

    .md-tile-text--primary {
      white-space: normal;
    }
  }

  .md-dialog--centered-enter {
    @if $md-dialog-use-flex-positioning {
      transform: translate3d(0, $md-dialog-translate-distance, 0);
    } @else {
      transform: translate3d(-50%, calc(-50% + #{$md-dialog-translate-distance}), 0);
    }

    &.md-dialog--centered-enter-active {
      @if $md-dialog-use-flex-positioning {
        transform: translate3d(0, 0, 0);
      } @else {
        transform: translate3d(-50%, -50%, 0);
      }

      transition-duration: $md-dialog-transition-time;
      transition-property: transform;
    }
  }

  .md-dialog--centered-leave {
    @if $md-dialog-use-flex-positioning {
      transform: translate3d(0, 0, 0);
    } @else {
      transform: translate3d(-50%, -50%, 0);
    }

    &.md-dialog--centered-leave-active {
      @if $md-dialog-use-flex-positioning {
        transform: translate3d(0, $md-dialog-translate-distance, 0);
      } @else {
        transform: translate3d(-50%, calc(-50% + #{$md-dialog-translate-distance}), 0);
      }

      transition-duration: $md-dialog-transition-time;
      transition-property: transform;
    }
  }

  .md-dialog--full-page {
    // Don't add scroll momentum here since it'll make fixed toolbars buggy.
    bottom: 0;
    left: 0;
    overflow: auto;
    top: 0;
    width: 100vw;
    z-index: $md-dialog-full-page-z-index;
  }

  .md-dialog--full-page-enter {
    transform: scale(0);

    &.md-dialog--full-page-enter-active {
      transform: scale(1);
      transition-duration: $md-dialog-transition-time;
      transition-property: transform;
    }
  }

  .md-dialog--full-page-leave {
    transform: scale(1);

    &.md-dialog--full-page-leave-active {
      transform: scale(0);
      transition-duration: $md-dialog-transition-time;
      transition-property: transform;
    }
  }

  @include react-md-dialogs-header;
  @include react-md-dialogs-content;
  @include react-md-dialogs-footer;
}

@mixin react-md-dialogs-header {
  .md-title--dialog {
    margin-bottom: 0;
    padding: $md-dialog-padding;
    padding-bottom: $md-dialog-title-padding-bottom;
    white-space: normal;
  }
}

@mixin react-md-dialogs-content {
  .md-dialog-content {
    @include md-ios-scroll-momentum;

    overflow: auto;
  }

  .md-dialog-content--padded {
    padding: $md-dialog-padding;

    &:not(:first-child) {
      padding-top: 0;
    }
  }
}

@mixin react-md-dialogs-footer {
  .md-dialog-footer {
    display: flex;
    justify-content: flex-end;
  }

  .md-dialog-footer--inline {
    padding: $md-dialog-btn-padding;

    .md-btn--dialog + .md-btn--dialog {
      margin-left: $md-dialog-btn-padding;
    }
  }

  .md-dialog-footer--stacked {
    align-items: flex-end;
    flex-direction: column;
    padding-bottom: $md-dialog-btn-padding;
    padding-right: $md-dialog-btn-padding;

    .md-btn--dialog {
      $margin: (48px - $md-dialog-btn-height) / 2;

      margin-bottom: $margin;
      margin-top: $margin;
    }
  }

  .md-btn--dialog {
    height: $md-dialog-btn-height;
    min-width: $md-dialog-btn-min-width;
    padding-left: $md-dialog-btn-padding;
    padding-right: $md-dialog-btn-padding;
  }
}

@mixin react-md-dialog-width($units: 5) {
  width: $units * 56px;
}

@mixin react-md-dividers($light-theme: $md-light-theme) {
  .md-divider {
    background: get-color('divider', $light-theme);
    border: 0;
    content: '';
    display: block;
    height: $md-divider-height;
    margin: 0;
  }

  .md-divider--vertical {
    height: 100%;
    width: $md-divider-height;
  }

  .md-divider--inset {
    margin-left: $md-list-left-padding;
  }

  @if $include-borders {
    @include react-md-divider-borders($light-theme);
  }
}

@mixin react-md-divider-borders($light-theme: $md-light-theme) {
  .md-divider-border {
    border-color: get-color('divider', $light-theme);
    border-style: solid;
    border-width: 0;
  }

  .md-divider-border--top {
    border-top-width: 1px;
  }

  .md-divider-border--right {
    border-right-width: 1px;
  }

  .md-divider-border--bottom {
    border-bottom-width: 1px;
  }

  .md-divider-border--left {
    border-left-width: 1px;
  }
}

@mixin react-md-theme-dividers($light-theme: $md-light-theme) {
  @if $light-theme != $md-light-theme {
    .md-divider {
      background: get-color('divider', $light-theme);
    }
  }

  @if $include-borders {
    @include react-md-theme-divider-borders($light-theme);
  }
}

@mixin react-md-theme-divider-borders($light-theme: $md-light-theme) {
  @if $light-theme != $md-light-theme {
    .md-divider-border {
      border-color: get-color('divider', $light-theme);
    }
  }
}

@mixin react-md-drawers($include-media: $md-media-included) {
  @if $include-media {
    @include react-md-drawers-media;
  }

  .md-drawer {
    transition-duration: $md-drawer-transition-time;
    transition-property: transform;

    &--fixed {
      bottom: 0;
      position: fixed;
      top: 0;
      z-index: $md-drawer-z-index;
    }

    &--inline {
      display: inline-block;
      height: 100%;
    }

    &--left {
      left: 0;
    }

    &--right {
      right: 0;
    }

    &--active {
      transform: translate3d(0, 0, 0);
    }

    &--mini {
      z-index: $md-drawer-z-index - 1;
    }
  }

  .md-list--drawer {
    @include md-ios-scroll-momentum;

    height: 100%;
    overflow-y: auto;
  }

  .md-overlay--drawer.md-overlay--drawer {
    transition-duration: $md-drawer-transition-time;
  }
}

@mixin react-md-drawers-mobile {
  .md-drawer {

    &--left {
      max-width: $md-drawer-mobile-max-width;
      transform: translate3d(-100%, 0, 0);
      width: calc(100vw - #{$md-drawer-mobile-margin});
    }

    &--right {
      left: 0;
      transform: translate3d(100%, 0, 0);
    }

    &--mini.md-drawer--mini {
      width: $md-drawer-mobile-mini-width;
    }
  }

  .md-list-tile--mini.md-list-tile--mini {
    padding-left: $md-drawer-mobile-mini-item-padding;
    padding-right: $md-drawer-mobile-mini-item-padding;
  }

  .md-drawer-relative--mini.md-drawer-relative--mini {
    margin-left: $md-drawer-mobile-mini-width;
  }

  .md-toolbar ~ .md-list--drawer {
    height: calc(100% - #{$md-toolbar-mobile-portrait-height});
  }
}

@mixin react-md-drawers-mobile-landscape {
  .md-toolbar ~ .md-list--drawer {
    height: calc(100% - #{$md-toolbar-mobile-landscape-height});
  }
}

@mixin react-md-drawers-tablet {
  .md-drawer {

    &--left {
