mixins.code:
@mixin react-md-media($include-overlay: $md-media-include-overlay, $include-embedded: $md-media-include-embedded, $embedded-selectors: $md-media-embedded-selectors) {
  .md-media {
    display: block;
    height: 0;
    overflow: hidden;
    padding: 0;
    position: relative;

    @if $embedded-selectors and length($embedded-selectors) {
      @each $selector in $embedded-selectors {
        #{$selector} {
          @extend %md-media-embedded;
        }
      }
    }
  }

  @each $aspect-ratio in $md-media-aspect-ratios {
    $map: extract-media($aspect-ratio);
    $width: map-get($map, width);
    $height: map-get($map, height);

    @include react-md-media-aspect-ratio($width, $height);
  }

  @if $include-overlay {
    @include react-md-media-overlay;
  }

  @if $include-embedded {
    @include react-md-media-embedded;
  }
}

@mixin react-md-media-aspect-ratio($width, $height) {
  .md-media--#{$width}-#{$height} {
    padding-bottom: percentage($height / $width);
  }
}

@mixin react-md-media-embedded {
  .md-media-embedded {
    @extend %md-media-embedded;
  }
}

@mixin react-md-media-overlay {
  .md-media-overlay {
    background: $md-media-overlay-color;
    bottom: 0;
    position: absolute;
    width: 100%;
    z-index: 1;

    .md-btn,
    .md-text {
      color: get-color('text', false);
    }

    .md-text--secondary {
      color: get-color('secondary', false);
    }
  }
}

@mixin react-md-menus($include-media: $md-media-included, $include-cascading: $md-menu-include-cascading) {
  .md-list--menu {
    max-width: $md-menu-max-width;
    min-width: $md-menu-min-width;
    z-index: $md-menu-z-index;

    &-restricted {
      @include md-ios-scroll-momentum;

      max-height: $md-menu-max-height;
      overflow-y: auto;
    }

    &-contained {
      width: 100%;
    }

    &-below {
      left: 0;
      top: 100%;
      transform-origin: 50% 0;
    }

    &-tr {
      right: 0;
      top: 0;
      transform-origin: 100% 0;
    }

    &-tl {
      top: 0;
      transform-origin: 0 0;
    }

    &-br {
      right: 0;
      transform-origin: 100% 100%;
    }

    &-bl {
      transform-origin: 0 100%;
    }
  }

  @if $include-cascading {
    @include react-md-menus-cascading($include-cascading-styles);
  }

  @if $include-media {
    @include react-md-menus-media;
  }
}

@mixin react-md-menus-cascading($include-cascading-styles: $md-menu-include-cascading-styles) {
  @media #{$md-desktop-media} {
    .md-list--menu-cascading {
      @if $include-cascading-styles {
        padding-bottom: $md-menu-cascading-list-padding;
        padding-top: $md-menu-cascading-list-padding;

        .md-list-tile {
          padding-left: $md-menu-cascading-padding;
          padding-right: $md-menu-cascading-padding;

          &:not(.md-list-tile--two-lines):not(.md-list-tile--three-lines) {
            height: $md-menu-cascading-height;
          }
        }

        .md-tile-text--primary {
          font-size: $md-menu-cascading-font-size;
        }
      }

      .md-collapser {
        transform: rotate3d(0, 0, 1, -90deg);
      }

      .md-collapser--flipped {
        transform: rotate3d(0, 0, 1, 90deg);
      }
    }
  }
}

@mixin react-md-menus-mobile {
  @if $md-menu-max-width == null or $md-menu-min-width == null {
    .md-list--menu {
      max-width: if($md-menu-max-width == null, $md-menu-mobile-max-width, null);
      min-width: if($md-menu-min-width == null, $md-menu-mobile-min-width, null);
    }
  }

  @if $md-menu-max-height == null {
    .md-list--menu-restricted {
      max-height: $md-menu-mobile-max-height;
    }
  }
}

@mixin react-md-menus-desktop {
