mixins.code:
@mixin react-md-pickers($primary-color: $md-primary-color, $light-theme: $md-light-theme, $include-media: $md-media-included, $include-date: $md-picker-include-date, $include-time: $md-picker-include-time, $include-forceful-classes: $md-picker-include-forceful-classes) {
  @if $include-date or $include-time {
    @include react-md-pickers-base($primary-color, $light-theme, $include-forceful-classes);

    @if $include-media {
      @include react-md-pickers-media($include-date, $include-time);
    }
  }

  @if $include-date {
    @include react-md-pickers-date($primary-color, $light-theme);
  }

  @if $include-time {
    @include react-md-pickers-time($primary-color, $light-theme);
  }
}

@mixin react-md-pickers-base($primary-color: $md-primary-color, $light-theme: $md-light-theme, $include-forceful-classes: $md-picker-include-forceful-classes) {
  .md-picker-container {
    position: relative;
  }

  .md-picker-content-container {
    background: if($light-theme, get-color('card', true), get-color('background', false));
  }

  .md-picker--inline {
    @include md-box-shadow(3);

    position: absolute;
    z-index: $md-picker-inline-z-index;
  }

  .md-picker--inline-icon {
    left: $md-font-icon-size + $md-default-padding;
  }

  .md-picker-control {
    @extend %md-text-left;

    padding: 0;
  }

  .md-picker-text {
    color: get-color('secondary', false);

    &.md-picker-text > * {
      color: inherit;
    }

    > * {
      font-weight: inherit;
      margin: 0;
      transition-duration: $md-transition-time;
      transition-property: color, font;
    }
  }

  .md-picker-text--active {
    color: get-color('text', false);
    font-weight: $md-font-medium;
  }

  .md-dialog--picker {
    @include md-ios-scroll-momentum;

    max-height: calc(100% - #{$md-picker-mobile-margin});
    max-width: calc(100% - #{$md-picker-mobile-margin});
    overflow: auto;
    width: auto;
  }

  .md-dialog-content--picker {
    padding: 0;
  }

  .md-picker-header {
    background: if($light-theme, $primary-color, get-color('card', false));
    padding: $md-dialog-padding;
  }

  @if $include-forceful-classes {
    @include react-md-pickers-force-orientation;
  }
}

@mixin react-md-pickers-force-orientation {
  .md-picker.md-picker--portrait {
    @include react-md-pickers-make-portrait;

    display: block;

    .md-picker-content {
      width: $md-picker-portrait-width;
    }

    &.md-picker--time .md-picker-header {
      padding-top: $md-dialog-padding;
    }

    .md-picker-header {
      width: 100%;
    }
  }

  .md-picker.md-picker--landscape {
    @include react-md-pickers-make-landscape;

    .md-picker-header {
      height: auto;
    }
  }
}

@mixin react-md-pickers-date($primary-color: $md-primary-color, $light-theme: $md-light-theme) {
  .md-picker--date {
    .md-picker-control {
      display: block;
    }
  }

  .md-picker-content--calendar {
    padding-left: 12px;
    padding-right: 12px;
  }

  .md-calendar-controls {
    align-items: center;
    display: flex;

    .md-title {
      flex-grow: 1;
      font-size: 16px;
      margin-bottom: 0;
      text-align: center;
    }
  }

  .md-calendar-date {
    @extend %md-inline-block;
    @extend %md-text-center;

    @if not $light-theme {
      color: get-color('secondary', false);

      .md-picker-text--active {
        color: get-color('background', false);
      }
    }

    margin: 0;
    width: calc(100% / 7);
  }

  .md-picker-content--year {
    @include md-ios-scroll-momentum;

    overflow-y: auto;
  }

  .md-years {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  .md-year {
    font-size: $md-year-font-size;
    padding: 12px;
    transition-duration: $md-transition-time;
    transition-property: color, font-size;

    &:focus:not(.md-year--active) {
      font-size: $md-year-focus-font-size;
    }

    &--active {
      font-size: $md-year-active-font-size;
      font-weight: $md-font-medium;
    }
  }

  .md-calendar-date--btn {

    &::after {
      background: $primary-color;
      border-radius: 50%;
      content: '';
      display: block;
      left: 50%;
      position: absolute;
      top: 50%;
      transform: translateX(-50%) translateY(-50%) scale(0);
      transition-duration: $md-transition-time;
      transition-property: transform;
      z-index: 0;
    }
  }

  .md-calendar-date--btn-active {
    font-weight: $md-font-bold;

    &::after {
      transform: translateX(-50%) translateY(-50%) scale(1);
    }
  }

  .md-calendar-date--date {
    position: relative;
    z-index: 1;
  }

  .md-calendar-dow {
    font-size: $md-calendar-date-font-size;
    font-weight: $md-font-bold;
    line-height: $md-line-height;
  }
}

@mixin react-md-pickers-time($primary-color: $md-primary-color, $light-theme: $md-light-theme) {
  .md-picker-content--clock {
    padding: $md-dialog-padding;
    padding-bottom: $md-dialog-padding / 2;
  }

  .md-time-periods .md-picker-control {
    display: block;
  }

  .md-time-period {
    font-size: 16em / $md-font-size-base;
    font-weight: $md-font-medium;
    margin: 0;
    padding: 0;
  }

  .md-clock-face {
    background: if($light-theme, $md-grey-100, get-color('card', false));
    border-radius: 50%;
    position: relative;
  }

  .md-clock-hand {
    height: 2px;
    position: absolute;
    transform-origin: 0 0;
    transition-duration: $md-transition-time;
    transition-property: width;
    z-index: 1;

    &--active {
      transition-property: transform, width;
    }

    &::before,
    &::after {
      background: $primary-color;
      border-radius: 50%;
      content: '';
      position: absolute;
    }

    &::before {
      height: $md-clock-hand-ball-size;
      left: -$md-clock-hand-ball-size / 2;
      // no idea why
      top: -2px;
      width: $md-clock-hand-ball-size;
    }

    &::after {
      height: $md-clock-time-size;
      right: -$md-clock-time-size / 2 + $md-clock-padding / 2;
      top: -$md-clock-time-size / 2;
      transition-duration: $md-transition-time;
      transition-property: background, border, transform;
      width: $md-clock-time-size;
    }

    &--minute-hover::after {
      transform: scale($md-clock-hand-ball-scale);
    }
  }

  .md-clock-time {
    align-items: center;
    display: flex;
    height: $md-clock-time-size;
    justify-content: center;
    position: absolute;
    width: $md-clock-time-size;
    z-index: 8;

    &:focus {
      outline-style: none;
    }
  }

  .md-clock-time-value {
    @extend %md-font-medium;
  }
}

@mixin react-md-pickers-make-portrait($include-date: $md-picker-include-date, $include-time: $md-picker-include-time) {
  width: $md-picker-portrait-width;

  .md-picker-header {
    height: $md-picker-portrait-header-height;
  }

  .md-time-periods {
    display: inline-block;
    padding-left: 1em;
    padding-right: $md-dialog-padding;
  }

  .md-picker-content {
    height: $md-picker-portrait-content-height;
  }

  .md-display-3 {
    font-size: 5em;
    line-height: 1;
  }

  .md-calendar-dows {
    padding-bottom: $md-dialog-padding / 2;
  }

  @if $include-date {
    &.md-picker--date {
      .md-display-1 {
        display: inline-block;
      }
    }

    .md-calendar-date--btn {
      height: $md-date-picker-portrait-date-size;

      &::after {
        height: $md-date-picker-portrait-ball-size;
        width: $md-date-picker-portrait-ball-size;
      }
    }
  }

  @if $include-time {
    .md-clock-face {
      height: $md-clock-portrait-size;
      width: $md-clock-portrait-size;
    }

    .md-clock-hand {
      width: ($md-clock-portrait-size - $md-clock-padding - $md-clock-time-size) / 2;

      &--inner {
        width: ($md-clock-portrait-size - $md-clock-padding - $md-clock-time-size * 3) / 2;
      }
    }
  }
}

@mixin react-md-pickers-make-landscape($include-date: $md-picker-include-date, $include-time: $md-picker-include-time) {
  display: flex;
  width: $md-picker-landscape-width;

  .md-picker-header {
    width: $md-picker-landscape-header-width;
  }

  .md-picker-content {
    height: $md-picker-landscape-content-height;
    width: $md-picker-landscape-width - $md-picker-landscape-header-width;
  }

  @if $include-date {
    .md-calendar-date--btn {
      height: $md-date-picker-landscape-date-size;

      &::after {
        height: $md-date-picker-landscape-ball-size;
        width: $md-date-picker-landscape-ball-size;
      }
    }
  }

  @if $include-time {
    &.md-picker--time .md-picker-header {
      padding-top: $md-picker-landscape-content-height / 3;
    }

    .md-clock-face {
      height: $md-clock-landscape-size;
      width: $md-clock-landscape-size;
    }

    .md-clock-hand {
      width: ($md-clock-landscape-size - $md-clock-padding - $md-clock-time-size) / 2;

      &--inner {
        width: ($md-clock-landscape-size - $md-clock-padding - $md-clock-time-size * 3) / 2;
      }
    }

    .md-time-periods {
      margin-left: auto;
      margin-right: 2.5em;
      width: 35px;
    }

    .md-display-3 {
      font-size: 3.25em;
    }
  }
}

@mixin react-md-pickers-media {
  @if $include-date or $include-time {
    @media (orientation: portrait) {
      .md-picker {
        @include react-md-pickers-make-portrait($include-date, $include-time);
      }
    }

    @media (orientation: landscape) {
      .md-picker {
        @include react-md-pickers-make-landscape($include-date, $include-time);
      }
    }

    @if $mobile-max-size {
      @media (max-width: $mobile-max-size) and (orientation: portrait) {
        .md-picker {
          width: $mobile-max-size - $md-picker-mobile-margin;
        }
      }

      @media (max-height: $mobile-max-size) and (orientation: landscape) {
        $md-dialog-footer-height-mobile: 64px;

        .md-picker {
          .md-picker-content {
            height: $mobile-max-size - $md-dialog-footer-height-mobile;
            width: calc(100vw - #{$md-picker-landscape-header-width + $md-picker-mobile-margin});
          }
        }
      }
    }
  }
}

@mixin react-md-theme-pickers($primary-color: $md-primary-color, $light-theme: $md-light-theme, $include-date: $md-picker-include-date, $include-time: $md-picker-include-time) {
  @if $include-date or $include-time {
    $themed: $light-theme != $md-light-theme;

    @if $primary-color != $md-primary-color {
      @if not $light-theme and $themed {
        .md-picker-header {
          background: get-color('card', false);
        }
      } @else if $light-theme {
        .md-picker-header {
          background: $primary-color;
        }
      }

      @if $include-date {
        .md-calendar-date--btn::after {
          background: $primary-color;
        }
      }

      @if $include-time {
        .md-clock-hand {
          &::before,
          &::after {
            background: $primary-color;
          }
        }
      }
    }

    @if $themed {
      .md-picker-content-container {
        background: if($light-theme, get-color('card', true), get-color('background', false));
      }

      @if $include-date {
        .md-calendar-date {
          color: get-color('secondary', false);

          .md-picker-text--active {
            color: get-color('background', false);
          }
        }
      }

      @if $include-time {
        .md-clock-face {
          background: if($light-theme, $md-grey-100, get-color('card', false));
        }
      }
    }
  }
}

@mixin react-md-progress($secondary-color: $md-secondary-color, $linear-swatch: $md-linear-progress-swatch, $include-circular: $md-progress-include-circular, $include-linear: $md-progress-include-linear) {
  @if $include-circular or $include-linear {
    .md-progress {
      display: block;
      margin-bottom: $md-progress-margin;
      margin-top: $md-progress-margin;
    }
  }

  @if $include-circular {
    @include react-md-progress-circular($secondary-color);
  }

  @if $include-linear {
    @include react-md-progress-linear($secondary-color);
  }
}

@mixin react-md-progress-circular($secondary-color: $md-secondary-color) {
  .md-progress--circular-determinate {
    transition-duration: $md-transition-time;
    transition-property: transform;
  }

  .md-progress--circular-indeterminate {
    animation-duration: $md-progress-duration;
    animation-iteration-count: infinite;
    animation-name: md-circular-progress;
    animation-timing-function: linear;
  }

  .md-circular-progress-path {
    fill: none;
    stroke: $secondary-color;
    stroke-dasharray: $md-circular-progress-stroke-dasharray;
  }

  .md-circular-progress-path--animated {
    animation-duration: $md-progress-duration;
    animation-iteration-count: infinite;
    animation-name: md-circular-progress-dash;
    animation-timing-function: ease-ine-out;
    stroke-dashoffset: 0;
  }

  @keyframes md-circular-progress {
    0% { transform: rotate(0deg); }

    50% { transform: rotate(135deg); }

    75% { transform: rotate(450deg); }

    100% { transform: rotate(720deg); }
  }

  @keyframes md-circular-progress-dash {
    0% {
      stroke-dashoffset: $md-circular-progress-stroke-dasharray;
    }

    50% {
      stroke-dashoffset: $md-circular-progress-stroke-dasharray / 4;
    }

    100% {
      stroke-dashoffset: $md-circular-progress-stroke-dasharray;
    }
  }
}

@mixin react-md-progress-linear($secondary-color: $md-secondary-color, $swatch: $md-linear-progress-swatch, $fallback-color: $md-linear-progress) {
  .md-progress--linear {
    background: get-swatch($secondary-color, $swatch, true, $fallback-color, md-linear-progress-fallback-color);
    height: $md-linear-progress-height;
    overflow: hidden;
    position: relative;
    width: 100%;
  }

  .md-progress--linear-active {
    background: $secondary-color;
  }

  .md-progress--linear-determinate {
    height: 100%;
    position: absolute;
    z-index: 1;
  }

  .md-progress--linear-indeterminate {
    &::before,
    &::after {
      background: inherit;
      bottom: 0;
      content: '';
      position: absolute;
      top: 0;
      will-change: left, right;
      z-index: 1;
    }

    &::before {
      animation-duration: $md-progress-duration;
      animation-iteration-count: infinite;
      animation-name: md-linear-indeterminate;
      animation-timing-function: $md-transition-standard;
    }

    &::after {
      animation-delay: $md-linear-progress-delay;
      animation-duration: $md-progress-duration;
      animation-iteration-count: infinite;
      animation-name: md-linear-indeterminate-short;
      animation-timing-function: $md-transition-acceleration;
    }
  }

  .md-progress--linear-query {
    &::before,
    &::after {
      animation-direction: reverse;
    }
  }


  @keyframes md-linear-indeterminate {
    0% {
      left: -35%;
      right: 100%;
    }

    60% {
      left: 100%;
      right: -90%;
    }

    100% {
      left: 100%;
      right: -90%;
    }
  }

  @keyframes md-linear-indeterminate-short {
    0% {
      left: -200%;
      right: 100%;
    }

    60% {
      left: 107%;
      right: -8%;
    }

    100% {
      left: 107%;
      right: -8%;
    }
  }
}

@mixin react-md-theme-progress($secondary-color: $md-secondary-color, $linear-swatch: $md-linear-progress-swatch, $include-circular: $md-progress-include-circular, $include-linear: $md-progress-include-linear, $fallback-color: $md-linear-progress) {
  @if $include-circular {
    @include react-md-theme-progress-circular($secondary-color);
  }

  @if $include-linear {
    @include react-md-theme-progress-linear($secondary-color, $linear-swatch, $fallback-color);
  }
}

@mixin react-md-theme-progress-circular($secondary-color: $md-secondary-color) {
  @if $secondary-color != $md-secondary-color {
    .md-circular-progress-path {
      stroke: $secondary-color;
    }
  }
}

@mixin react-md-theme-progress-linear($secondary-color: $md-secondary-color, $swatch: $md-linear-progress-swatch, $fallback-color: $md-linear-progress) {
  @if $secondary-color != $md-secondary-color {
    .md-progress--linear-active {
      background: $secondary-color;
    }
  }

  @if $secondary-color != $md-secondary-color or $swatch != $md-linear-progress-swatch or $md-linear-progress-fallback-color != $fallback-color {
    .md-progress--linear {
      background: get-swatch($secondary-color, $swatch, true, $fallback-color, md-linear-progress-fallback-color);
    }
  }
}

@mixin react-md-select-fields($include-media: $md-media-included) {
  .md-select-field {
    cursor: inherit;
    display: flex;
    flex-wrap: wrap;
    user-select: none;

    .md-divider {
      width: 100%;
    }

    &__toggle {
      position: relative;
    }
  }

  // To override text field padding
  .md-select-field--btn.md-select-field--btn {
    padding-bottom: $md-default-padding;
    padding-left: $md-select-field-left-padding;
    padding-right: $md-select-field-right-padding;
    padding-top: $md-default-padding;
  }

  .md-select-field--text-field {
    // without this, mobile safari will not bubble the click event up to the toggle element so the
    // options never appear.
    pointer-events: none;
  }

  @include react-md-select-fields-drop-transition;

  @if $include-media {
    @include react-md-select-fields-media;
  }
}

@mixin react-md-select-fields-drop-transition {
  .md-drop-enter {
    transform: translate3d(0, -$md-select-field-drop-distance, 0);

    &.md-drop-enter-active {
      transform: translate3d(0, 0, 0);
      transition-duration: $md-select-field-drop-transition-time;
      transition-property: transform;
    }
  }
}

@mixin react-md-select-fields-mobile {
  .md-select-field--btn {
    height: $md-text-field-mobile-height;
  }

  .md-select-field--text-field {
    height: 18px;
  }
}

@mixin react-md-select-fields-desktop {
  .md-select-field--btn {
    height: $md-text-field-desktop-height;
  }

  .md-select-field--text-field {
    height: 15px;
  }
}

@mixin react-md-select-fields-media {
  @media #{$md-mobile-media} {
    @include react-md-select-fields-mobile;
  }

  @media #{$md-desktop-media} {
    @include react-md-select-fields-desktop;
  }
}

@mixin react-md-selection-controls($secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $include-group: $md-selection-controls-include-group, $include-switches: $md-selection-controls-include-switches, $include-dense-icons: $md-font-icon-include-dense) {

  .md-selection-control-container--inline {
    display: inline-block;
  }

  .md-selection-control-input {
    height: 0;
    position: absolute;
    visibility: hidden;
    width: 0;
  }

  .md-selection-control-label {
    align-items: center;
    display: inline-flex;
  }

  @if $include-group {
    .md-selection-control-group {
      border: 0;
    }
  }

  @if $include-switches {
    @include react-md-selection-controls-switch($secondary-color, $light-theme);
  }

  @if $include-media {
    @include react-md-selection-controls-media($include-dense-icons);
  }
}

@mixin react-md-selection-controls-switch($secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $fallback-color: $md-switch-ball-fallback-color) {
  $swatch: get-swatch($secondary-color, if($light-theme, 500, 200), false, $fallback-color, md-switch-ball-fallback-color);

  .md-switch-container {
    align-items: center;
    display: flex;
    height: $md-switch-height;

    &.md-selection-control-container--inline {
      display: inline-flex;
    }
  }

  .md-switch-track {
    border-radius: $md-switch-track-radius;
    flex-shrink: 0;
    height: $md-switch-track-height;
    margin-left: 12px;
    margin-right: 12px;
    position: relative;
    width: $md-switch-track-width;
  }

  .md-switch-track--on {
    background: rgba($swatch, .5);
  }

  .md-switch-track--off {
    background: if($light-theme, $md-switch-light-theme-track-off, $md-switch-dark-theme-track-off);
  }

  .md-switch-track--disabled {
    background: if($light-theme, $md-switch-light-theme-track-disabled, $md-switch-dark-theme-track-disabled);
  }

  .md-switch-thumb {
    @include md-box-shadow(1);

    border-radius: 50%;
    content: '';
    display: block;
    height: $md-switch-thumb-size;
    position: absolute;
    transition-duration: $md-transition-time;
    transition-property: background, transform;
    width: $md-switch-thumb-size;
  }

  .md-switch-thumb--on {
    background: $swatch;
    transform: translate3d(#{$md-switch-track-width - $md-switch-thumb-size + $md-switch-thumb-offset}, -$md-switch-thumb-offset, 0);
  }

  .md-switch-thumb--off {
    background: if($light-theme, $md-switch-light-theme-thumb-off, $md-switch-dark-theme-thumb-off);
    transform: translate3d(-$md-switch-thumb-offset, -$md-switch-thumb-offset, 0);
  }

  .md-switch-thumb--disabled {
    background: if($light-theme, $md-switch-light-theme-thumb-disabled, $md-switch-dark-theme-thumb-disabled);
  }
}

@mixin react-md-selection-controls-mobile {
  .md-selection-control-label {
    font-size: $md-text-field-mobile-font-size;
  }
}

@mixin react-md-selection-controls-desktop($include-dense-icons: $md-font-icon-include-dense) {
  .md-selection-control-label {
    font-size: $md-text-field-desktop-font-size;
  }

  @if $include-dense-icons {
    .md-selection-control-container {
      height: $md-btn-icon-dense-size;
    }
  }
}

@mixin react-md-selection-controls-media($include-dense-icons: $md-font-icon-include-dense) {
  @media #{$md-mobile-media} {
    @include react-md-selection-controls-mobile;
  }

  @media #{$md-desktop-media} {
    @include react-md-selection-controls-desktop($include-dense-icons);
  }
}

@mixin react-md-theme-selection-controls($secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $include-switches: $md-selection-controls-include-switches, $fallback-color: $md-switch-ball-fallback-color) {
  @if $include-switches {
    @include react-md-theme-selection-controls-switch($secondary-color, $light-theme, $fallback-color);
  }
}

@mixin react-md-theme-selection-controls-switch($secondary-color: $md-secondary-color, $light-theme: $md-light-theme, $fallback-color: $md-switch-ball-fallback-color) {
  @if $secondary-color != $md-secondary-color or $fallback-color != $md-switch-ball-fallback-color {
    $swatch: get-swatch($secondary-color, if($light-theme, 500, 200), false, $fallback-color, md-switch-ball-fallback-color);

    .md-switch-track--on {
      background: rgba($swatch, .5);
    }

    .md-switch-thumb--on {
      background: $swatch;
    }
  }

  @if $light-theme != $md-light-theme {
    .md-switch-track--off {
      background: if($light-theme, $md-switch-light-theme-track-off, $md-switch-dark-theme-track-off);
    }

    .md-switch-track--disabled {
      background: if($light-theme, $md-switch-light-theme-track-disabled, $md-switch-dark-theme-track-disabled);
    }

    .md-switch-thumb--off {
      background: if($light-theme, $md-switch-light-theme-thumb-off, $md-switch-dark-theme-thumb-off);
    }

    .md-switch-thumb--disabled {
      background: if($light-theme, $md-switch-light-theme-thumb-disabled, $md-switch-dark-theme-thumb-disabled);
    }
  }
}

functions.code:
@function validate-slider-primary-color($primary-color, $light-theme) {
  @if $light-theme {
    @return true;
  }

  $found: false;
  @each $name, $color in $md-color-map {
    @if str-index($name, '-200') != null and str-index($name, '-a-200') == null and $color == $primary-color {
      @return true;
    }
  }

  $msg: "The current primary-color: '#{$primary-color}' is not a valid '-200' suffix for a dark-themed slider.";
  $msg: "#{$msg} This warning can be disabled by setting the '$md-slider-warn-md' or '$md-colors-warn-md' to 'false'.";
  @warn "#{$msg} See https://react-md.dev/v1/sassdoc/#sliders-variable-md-slider-warn-md for more information.";

  @return false;
}

mixins.code:
@mixin react-md-sliders($primary-color: $md-primary-color, $light-theme: $md-light-theme, $include-media: $md-media-included, $include-discrete: $md-slider-include-discrete, $include-editor: $md-slider-include-editor, $include-indicators: $md-slider-include-indicators) {
  @if $md-colors-warn-md and $md-slider-warn-md {
    $invalid-color: validate-slider-primary-color($primary-color, $light-theme);
  }

  $md-slider-on-color: $primary-color;
  $md-slider-off-color: if($light-theme, $md-slider-light-theme-off-color, $md-slider-dark-theme-off-color);
  $bg-color: if($light-theme, $md-white-base, $md-dark-theme-background-color);
  $md-slider-mask-ink-color: rgba($primary-color, $md-slider-mask-ink-opacity);

  .md-slider-container {
